import React from 'react';
import { FormattedMessage, useIntl } from 'react-intl';
import { SearchBox, SearchButton, SearchInputWrapper } from './SearchBox.style';

type InputProps = {
  type?: string;
  value?: number | string;
  buttonText?: string;
  inputClass?: string;
  buttonIcon?: React.ReactNode;
  placeholder?: React.ReactNode;
  bordered?: boolean;
  showSvg?: boolean;
  style?: React.CSSProperties;
  onChange?: (e: any) => void;
  onKeyPress?: (e: any) => void;
  onBlur?: (e: any) => void;
  onFocus?: (e: any) => void;
  onClick?: (e: any) => void;
};

const InputSearch: React.FC<InputProps> = ({
  type,
  value,
  buttonText,
  buttonIcon,
  onChange,
  style,
  onKeyPress,
  onBlur,
  onClick,
  onFocus,
  bordered,
  showSvg,
  inputClass,
}) => {
  const intl = useIntl();
  return (
    <>
      <SearchInputWrapper
        style={style}
        bordered={bordered}
        showSvg={showSvg}
        className={`${inputClass} ${bordered === true ? 'bordered' : ''}`}
      >
        <form>
          {showSvg && <span className='searchIcon'>{buttonIcon}</span>}
          <SearchBox
            type={type}
            value={value}
            placeholder={intl.formatMessage({
              id: 'searchPlaceholder',
              defaultMessage: 'Search your products from here',
            })}
            onChange={onChange}
            onFocus={onFocus}
            onKeyPress={onKeyPress}
            onBlur={onBlur}
          />
          {showSvg !== true ? (
            <SearchButton>
              {buttonIcon}
              {buttonText !== '' || null ? (
                <span className='buttonText'>
                  <FormattedMessage
                    id='searchButtonText'
                    defaultMessage={buttonText}
                  />
                </span>
              ) : (
                ''
              )}
            </SearchButton>
          ) : (
            ''
          )}
        </form>
      </SearchInputWrapper>
    </>
  );
};

export default InputSearch;
